mapper.update:
update ${tableName}
set user_code_status = 0
where ${column} = #{parttern} and user_code_status = 1

mapper.select:
select count(1)
from user_sub_email
where user_email = #{inputAccount}
and user_code = #{code}
and user_code_status = 1

select count(1)
from user_sub_phone
where user_mail = #{inputAccount}
and user_code = #{code}
and user_code_status = 1

select user_id
from user_sub
where user_email = #{inputAccount}

select user_id
from user_sub
where user_phone = #{inputAccount}
